INSERT INTO `r_app_gallery_images` VALUES(214, 71, '1.jpg', '<h1>Chambre Vue Mer</h1>\n\n<div class="span2">Chambre double cosy et confortable face port et mer avec balcon ou terrasse.</div>\n\n<p>Elle se veut moderne et classique avec salle de bain douche ou chaleureuse, &agrave; l&rsquo;anglaise avec sa grande salle de bain double vasque et baignoire, selon disponibilit&eacute;.</p>\n\n<p>Grand lit ou 2 lits jumaux selon disponibilit&eacute;.</p>\n\n <p><span >(lit suppl&eacute;mentaire en option pour une <strong>troisi&egrave;me personne</strong> au tarif de <strong>25 Euros par nuit</strong>)</span></p>\n\n<div class="row">\n<div class="span3"><strong>Am&eacute;nagements:</strong>\n\n<ul>\n <li>Espace Bureau avec Connexion Internet</li>\n <li>Wifi Gratuit</li>\n <li>Climatisation individuelle</li>\n <li>Chauffage individuel</li>\n <li>T&eacute;l&eacute;vision</li>\n <li>Canal Sat</li>\n <li>Animaux accept&eacute;s</li>\n <li>Grand Lit</li>\n <li>Lits jumeaux</li>\n <li>Coffre Fort</li>\n</ul>\n</div>\n\n<div class="span3"><strong>Chambres disponibles :</strong> 7<br />\n<strong>Clients Maximum :</strong> 3<br />\n<strong>lits :</strong> 1<br />\n<strong>Salle de bains :</strong> 1<br />\n<strong>Disponibilit&eacute; :</strong> disponible</div>\n</div>\n\n <div id="__if72ru4sdfsdfrkjahiuyi_once" >&nbsp;</div>\n\n <div id="__if72ru4sdfsdfruh7fewui_once" >&nbsp;</div>\n\n<div id="__zsc_once">&nbsp;</div>\n\n <div id="__hggasdgjhsagd_once" >&nbsp;</div>\n');
