INSERT INTO `r_app_home_value` VALUES(113, 300, 4263, 'http://www.intelithub.com', 'milan.intelithub@gmail.com', '8401494969', 'Ganesh Meredian ', 'sola', 'Ahmedabad', 'gujarat', NULL, '380061', NULL, NULL, 0, NULL, NULL, NULL, '<ol>\n <li>\n  <div class="_cgc kno-fb-ctx" >\n <div class="kno-rdesc">India is a vast South Asian country with diverse terrain &ndash; from Himalayan peaks to Indian Ocean coastline &ndash; and history reaching back 5 millennia. In the north, Mughal Empire landmarks include Delhi&rsquo;s Red Fort complex, massive Jama Masjid mosque and Agra&rsquo;s iconic Taj Mahal mausoleum. Pilgrims bathe in the Ganges in Varanasi, and Rishikesh is a yoga center and base for Himalayan trekking.</div>\n </div>\n </li>\n</ol>\n', NULL, NULL, NULL, 'Veer.png');
