INSERT INTO `r_app_home_value` VALUES(128, 315, 4467, 'www.florelia.fr', 'gothathelion@hotmail.com', '33.1.44.94.14.70', '8 Place Vendôme', '', 'PARIS', 'Ile De France', NULL, '33100', NULL, NULL, 0, NULL, NULL, NULL, '<div><big><strong>{{-FLORELIA-}}</strong></big></div>\n\n<div>&nbsp;</div>\n\n<div>\n<div><strong>L&rsquo;&eacute;tat d&rsquo;esprit de bouledeneige.fr est unique et d&eacute;passe la relation commerciale traditionnelle. La passion avec laquelle nous nous attachons &agrave; faire notre m&eacute;tier est fondamentale et refl&egrave;te notre envie de vous faire partager les &eacute;motions que nous avons imagin&eacute;es en cr&eacute;ant votre bouquet. Le temps que nous passons, l&rsquo;attention que nous portons au moindre d&eacute;tail, pour la plus simple ou la plus exceptionnelle des compositions t&eacute;moignent de notre philosophie du travail bien fait.</strong></div>\n</div>\n', NULL, NULL, NULL, 'FloreliaLogoWhitetransparentbg-01.png');
INSERT INTO `r_app_home_value` VALUES(129, 316, 4483, 'www.venus.com', 'gothathelion@hotmail.com', '1234567889', '2 rue du Faubourg St. Honoré', '', 'PARIS', 'Ile De France', NULL, '33100', NULL, NULL, 0, NULL, NULL, NULL, '<p>&nbsp;</p>\n\n <p><span ><span ><strong>VENUS<br />\nspa + beaut&eacute;</strong></span></span></p>\n\n<p>----------------------------------------------------------------</p>\n\n<p>VENUS a &eacute;lu l&#39;Institut de Beaut&eacute; comme le seul lieu professionnel pour une pratique s&eacute;rieuse de ses m&eacute;thodes de soins par l&#39;interm&eacute;diaire d&#39;esth&eacute;ticiennes qualifi&eacute;es.</p>\n\n<p><strong>Les femmes ont donc trouv&eacute; dans l&rsquo;Institut de Beaut&eacute; un lieu professionnel o&ugrave; l&rsquo;esth&eacute;ticienne, tel un &laquo; Docteur en Beaut&eacute; &raquo;, prescrit le produit le mieux adapt&eacute; &agrave; leur peau.</strong></p>\n\n<p>Aujourd&rsquo;hui VENUS est devenu &quot;la r&eacute;f&eacute;rence&quot; de l&rsquo;Institut de Beaut&eacute;.</p>\n\n<p>VENUS b&eacute;n&eacute;ficie d&rsquo;une pr&eacute;sence internationale : 11 000 Instituts r&eacute;partis dans 70 pays &agrave; travers le monde, dont 1 800 Instituts de Beaut&eacute; en France qui pratiquent les protocoles de soins exclusifs VENUS et proposent les produits conseill&eacute;s et prescrits par les esth&eacute;ticiennes aux consommatrices.</p>\n\n<div id="__if72ru4sdfsdfrkjahiuyi_once">&nbsp;</div>\n\n<div id="__if72ru4sdfsdfruh7fewui_once">&nbsp;</div>\n\n<div id="__zsc_once">&nbsp;</div>\n\n<div id="__hggasdgjhsagd_once">&nbsp;</div>\n', NULL, NULL, NULL, 'venuslogoWHITE-01.png');
INSERT INTO `r_app_home_value` VALUES(130, 317, 4499, 'http://www.coupeido.com/', 'gothathelion@hotmail.com', '1234567889', '39 Rue Maréchal Leclerc', '', 'PARIS', 'Ile De France', NULL, '33100', NULL, NULL, 0, NULL, NULL, NULL, '<p><span ><strong>Bienvenue chez COUPEIDO - Coiffeur et Visagiste &agrave; Paris.</strong></span></p>\n\n <p><span ><strong>B&eacute;n&eacute;ficiez d&rsquo;un service sur-mesure, avec une &eacute;quipe qui vous comprend parfaitement.</strong></span></p>\n\n <p><span ><strong>Nous nous effor&ccedil;ons de toujours offrir une prestation de qualit&eacute;, un service amical et personnalis&eacute;</strong></span></p>\n\n <p><span ><strong>Pr&eacute;sentation de ce couple de coiffeur Parisien&nbsp;:</strong></span></p>\n\n <p><span ><strong>Experts coiffeurs visagistes&nbsp;depuis plus de 30 ans, Pierre et Natacha vous accueillent dans leur&nbsp;salon de coiffure &agrave; Paris dans le &nbsp;15<sup>&egrave;me </sup>Arrondissement. Sp&eacute;cialiste de la coloration, Pierre aime travailler la couleur, il le doit aussi &agrave; sa double vie, puisqu&rsquo;en marge de son m&eacute;tier de coiffeur, il est <a href="http://fotologue.jp/pierre-lavenu" target="_blank" title="Pierre Lavenu est un artiste peintre contemporain qui propose la vente en ligne de ses toiles à des collectionneurs">aquarelliste</a>.</strong></span></p>\n\n <p><span ><strong>Le couple Pierre et Natacha ont travaill&eacute; tous les deux avec Jean-Marc Maniatis dont ils partagent les aspirations d&rsquo;artisan et de cr&eacute;atif.</strong></span></p>\n\n <p><span ><strong>Pour compl&eacute;ter le portrait de Pierre, nous pouvons mentionner son attirance pour le Japon qui l&rsquo;inspire profond&eacute;ment, notamment dans sa philosophie de travail et de vie, jusqu&rsquo;au nom de son salon.</strong></span></p>\n\n <p><span ><strong>Pour celles et ceux qui souhaitent des conseils avis&eacute;s et d&rsquo;une coupe qui colle parfaitement &agrave; leur personnalit&eacute;&nbsp;!</strong></span></p>\n\n <p><span ><strong>Chez Coupeido, vous n&rsquo;&ecirc;tes pas une cliente parmi tant d&rsquo;autres, vous serez consid&eacute;r&eacute;s&nbsp;! &nbsp;Chaque coup de ciseaux compte.</strong></span></p>\n\n <p><span ><strong>Salon de Coiffure Paris 15. Coupeido votre coiffeur visagiste pour Femme &amp; Homme &agrave; Paris. </strong></span></p>\n\n <p><span ><strong>&gt; &gt; Ouvert du Mardi au Samedi (ferm&eacute; le lundi) voir nos <a href="http://www.coupeido.com/horaires-plan/" title="Horaires & Plan">horaires d&rsquo;ouverture</a>. T&eacute;l&eacute;phone&nbsp;: 01 43 06 86 86</strong></span></p>\n\n <div id="__if72ru4sdfsdfrkjahiuyi_once"><span >&nbsp;</span></div>\n\n <div id="__if72ru4sdfsdfruh7fewui_once"><span >&nbsp;</span></div>\n\n <div id="__zsc_once"><span >&nbsp;</span></div>\n\n <div id="__hggasdgjhsagd_once"><span >&nbsp;</span></div>\n', NULL, NULL, NULL, 'Logocoupeido100x100white-01.png');
