INSERT INTO `r_app_infotab_values` VALUES(11, 139, 1959, 'Bertolino Pizzeria propose des pizzas à base de produit frais et cuites au feu de bois...', '<p>&lt;!--[if gte mso 9]>&lt;xml&gt;\r\n <o:OfficeDocumentSettings>\r\n  <o:AllowPNG/>\r\n </o:OfficeDocumentSettings>\r\n&lt;/xml&gt;&lt;![endif]--&gt;</p>\r\n\r\n <p><span >D&eacute;couvrez nos pizzas au feu de bois et notre sp&eacute;cialit&eacute;: les flammenk&uuml;che sal&eacute;es et sucr&eacute;es &agrave; volont&eacute; le jeudi midi uniquement sur place!<br />\r\nSur place et &agrave; emporter, laissez-vous tenter !<br />\r\n<br />\r\nVous retrouverez les pizzas les plus classiques aux exp&eacute;riences culinaires les plus d&eacute;licieuses avec nos sp&eacute;cialit&eacute;s et nos pizzas gastronomiques! Notre r&eacute;putation s&#39;est construite gr&acirc;ce &agrave; notre cuisson &agrave; l&#39;ancienne au four &agrave; bois et &agrave; l&#39;utilisation de produits frais!<br />\r\n<br />\r\n<br />\r\nLe midi, arr&ecirc;tez-vous et demandez le menu du jour!&nbsp;<br />\r\n<br />\r\nSur place et &agrave; emporter !<br />\r\n<br />\r\nPates, Lasagnes, Brochettes, Croque Mr, Sub, Sandpit&#39;z, Frites, Salades et autres assortiments maisons vous permettront d&#39;attaquer votre apr&egrave;s-midi avec &eacute;nergie et satisfaction d&#39;avoir bien mang&eacute;.</span>)</p>\r\n\r\n<p>&nbsp;</p>\r\n&lt;!--[if gte mso 9]>&lt;xml&gt;\r\n <w:WordDocument>\r\n  <w:View>Normal</w:View>\r\n  <w:Zoom>0</w:Zoom>\r\n  <w:TrackMoves/>\r\n  <w:TrackFormatting/>\r\n  <w:PunctuationKerning/>\r\n  <w:ValidateAgainstSchemas/>\r\n  <w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n  <w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n  <w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n  <w:DoNotPromoteQF/>\r\n  <w:LidThemeOther>FR</w:LidThemeOther>\r\n  <w:LidThemeAsian>X-NONE</w:LidThemeAsian>\r\n  <w:LidThemeComplexScript>X-NONE</w:LidThemeComplexScript>\r\n  <w:Compatibility>\r\n   <w:BreakWrappedTabl