INSERT INTO `r_app_infotab_values` VALUES(40, 320, 4548, 'Idées Séjour', '<p>LES 10 R&Egrave;GLES DU &quot;SAVOIR-CAMPER&quot;</p>\n\n<p>--------------------------------------------------------------<br />\nCamper dans les Campings Indigo, c&rsquo;est partager un certain art de vivre, en plein air. C&rsquo;est aussi savoir vivre en communaut&eacute;, en respectant ses voisins et certaines r&egrave;gles pour vivre ensemble. Pour que chacun soit heureux de ses vacances parmi nous, il suffit de respecter quelques r&egrave;gles...</p>\n\n<p>--------------------------------------------------------------</p>\n\n<p>1) Respectez l&rsquo;espace naturel o&ugrave; vous campez, les arbres, les arbustes, le sol ...</p>\n\n<p>2) Respectez les installations communes et signalez &agrave; l&rsquo;accueil tout<br />\ndysfonctionnement.</p>\n\n<p>3) Maintenez les espaces communs propres.</p>\n\n<p>4) Maintenez la propret&eacute; des sanitaires apr&egrave;s les avoir utilis&eacute;s en utilisant les raclettes.</p>\n\n<p>5) Ne faites pas de bruit apr&egrave;s 23h afin de respecter le sommeil de vos voisins.</p>\n\n<p>6) Circulez &agrave; vitesse r&eacute;duite pour la s&eacute;curit&eacute; des pi&eacute;tons et des enfants.</p>\n\n<p>7) Surveillez vos enfants,vous en &ecirc;tes responsables.</p>\n\n<p>8) Tenez vos chiens en laisse et ne les laissez jamais seuls dans la journ&eacute;e.</p>\n\n<p>9) Triez et d&eacute;posez vos d&eacute;chets dans les containers pr&eacute;vus &agrave; cet effet.</p>\n\n<p>10) Et surtout, profitez pleinement de votre s&eacute;jour parmi nous dans nos Campings Indigo.</p>\n', 'Active');
