INSERT INTO `r_app_service_details` VALUES(53, 292, 4175, '', 'Espace bien-être', 0, 'bienetre.jpg', '<h2>Des jacuzzis privatifs, un service exceptionnel !</h2>\n\n<p>Evadez-vous dans deux univers totalement diff&eacute;rents en toute intimit&eacute;. Choisissez votre ambiance et profitez d&rsquo;un massage relaxant dans un bain d&rsquo;eau chaude &agrave; 35&deg;C. Ces&nbsp;spas&nbsp;aux larges dimensions&nbsp;invitent &agrave; la convivialit&eacute; et vous procurent de v&eacute;ritables bienfaits th&eacute;rapeutiques.</p>\n\n<h2>Un hammam avec aromath&eacute;rapie pour une ambiance zen !</h2>\n\n<p>Le hammam, bain de vapeur humide, est un v&eacute;ritable soin de beaut&eacute; &agrave; l&rsquo;orientale. R&eacute;put&eacute; pour ses vertus relaxantes et apaisantes, il procure bien-&ecirc;tre et d&eacute;tente.</p>\n\n<h2>Des saunas pour une d&eacute;licieuse sensation de bien-&ecirc;tre !</h2>\n\n<p>Le sauna&nbsp;est une activit&eacute; traditionnelle en Finlande. Les bienfaits d&rsquo;un bain de vapeur s&egrave;che sur l&rsquo;organisme ne sont plus &agrave; d&eacute;montrer. La chaleur d&eacute;gag&eacute;e favorise la relaxation et la d&eacute;tente. Il est la solution id&eacute;ale, naturelle et &eacute;cologique contre les affections musculaires.<br />\nEquip&eacute; de four &agrave; pierres volcaniques, le sauna vous propose diverses options pour r&eacute;gler &agrave; votre convenance la temp&eacute;rature de votre cabine&nbsp;: chaleur tr&egrave;s s&egrave;che, douce et humide. Ainsi, vous profiterez d&rsquo;inoubliables moments de relaxation dans votre sauna.</p>\n\n<h2>Des modelages du monde pour vous d&eacute;tendre !</h2>\n', NULL, NULL, 'Active');
INSERT INTO `r_app_service_details` VALUES(54, 303, 4312, '', 'Conseilles', 30, '', '<p>Venez nous pondres vos doleances dans les oreilles!!! Si vous payez, nous vous ecoutons!</p>\n\n<div id="__if72ru4sdfsdfrkjahiuyi_once">&nbsp;</div>\n\n<div id="__if72ru4sdfsdfruh7fewui_once">&nbsp;</div>\n\n<div id="__hggasdgjhsagd_once">&nbsp;</div>\n', NULL, NULL, 'Active');
